import { MOVIEDATA } from "../actions/actionTypes";

const initialState = null;

const movieListReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case MOVIEDATA.UPDATE:
      return { ...state, ...payload };
    default:
      return state;
  }
};

export default movieListReducer;
