import React, { useState } from "react";

/**
 * @description - Header Component.
 * @returns {Node} - Header View Component
 */
function Header() {
  const [showSearchField, setSearchFieldStatus] = useState(false);
  return (
    <nav class="flex flex-row">
      <div className="flex-initial flex-shrink-0">
        <img
          src="https://test.create.diagnal.com/images/Back.png"
          alt="back icon"
          className=""
        />
      </div>
      <div className="">Romantic Comedy</div>
      <div class="flex justify-end items-end">
        <img
          src="https://test.create.diagnal.com/images/search.png"
          alt="search icon"
          className={`${showSearchField ? "hidden" : ""}`}
          onClick={() => setSearchFieldStatus(true)}
        />
        <input
          type="text"
          name="name"
          placeholder="Search..."
          className={`${!showSearchField ? "hidden" : ""}`}
        />
      </div>
    </nav>
  );
}

export default Header;
