import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import GridItem from "../components/GridItem";
import useFetch from "../hooks/useFetch";
import { setMovieData } from "../../store/actions";

function MovieList(props) {
  const [page, setPage] = useState(1);
  const { data, loading, error, refetch } = useFetch(
    "https://test.create.diagnal.com/data/page1.json"
  );
  const [movieData, setMovieData] = useState([]);

  console.log("response++++", data);

  useEffect(() => {
    if (Object.keys(data).length > 0) {
      dispatch(setMovieData(data));
      setMovieData(data?.["content-items"]?.content || []);
    }
  }, [data]);

  return (
    <div className="w-full p-4 grid gap-4 grid-cols-3 sm:grid-cols-4 md:grid-cols-6 lg:grid-cols-8 xl:grid-cols-10 2xl:grid-cols-12">
      {imgList.map((imgObj, index) => {
        return (
          <GridItem
            key={`${imgObj.name.replace(" ", "")}_${index}`}
            item={imgObj}
          />
        );
      })}
    </div>
  );
}

MovieList.propTypes = {};

export default MovieList;
