import { GRIDFILTER } from "../actions/actionTypes";

const initialState = {
  searchTerm: "",
};

const gridFilterReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case GRIDFILTER.FILTER:
      return { ...state, searchTerm: payload };
    default:
      return state;
  }
};

export default gridFilterReducer;
