import React, { useState, useRef, useCallback } from "react";
import GridItem from "../components/GridItem";
import useFetch from "../hooks/useFetch";
import { updateMovieList } from "../store/actions";
import { useDispatch, useSelector } from "react-redux";

function MovieList(props) {
  const dispatch = useDispatch();
  const [pageNumber, setPageNumber] = useState(1);
  const { data, loading, error, refetch } = useFetch(
    "https://test.create.diagnal.com/data/page1.json",
    1
  );
  const [movieData, setMovieData] = useState([]);
  console.log("response++++", data);

  useEffect(() => {
    if (data) {
      dispatch(updateMovieList(data));
      const pageItems = data?.["content-items"]?.content || [];
      setMovieData([...movieData, ...pageItems]);
    }
  }, [data]);

  return (
    <div className="w-full p-4 grid gap-4 grid-cols-3 sm:grid-cols-4 md:grid-cols-6 lg:grid-cols-8 xl:grid-cols-10 2xl:grid-cols-12">
      {movieData.map((item, index) => {
        return (
          <GridItem
            key={`${item.name.replaceAll(" ", "-").toLowerCase()}-${index}`}
            item={item}
          />
        );
      })}
    </div>
  );
}

export default MovieList;
