export const debounce = (fn, d) => {
  let timer;
  return function () {
    let context = this,
      args = arguments;
    clearTimeout(timer);
    timer = setTimeout(() => {
      fn.apply(context, args);
    }, d);
  };
};

const lowercasetext = (text) => text.toLowerCase();

export const filterDataByName = (term, array) =>
  array.filter(({ name }) => lowercasetext(name).includes(lowercasetext(term)));
