const Loader = ({ color }) => {
  return (
    <div class="flex items-center justify-center ">
      <div
        class={`w-16 h-16 border-b-2 border-${color}-900 rounded-full animate-spin`}
      ></div>
    </div>
  );
};

Loader.defaultProps = {
  item: {},
};

Loader.propTypes = {
  item: PropTypes.shape({
    name: PropTypes.string,
    "poster-image": PropTypes.string,
  }),
};

export default Loader;
