import React, { useState } from "react";

/**
 * @description - Header Component.
 * @returns {Node} - Header View Component
 */
function index(props) {
  const [showSearchField, setSearchFieldStatus] = useState(false);
  return (
    <nav class="flex items-center justify-between flex-wrap bg-teal-500 p-6">
      <img
        src="https://test.create.diagnal.com/images/Back.png"
        alt="back icon"
        className="w-5 h-5 transition duration-500 ease-in-out transform hover:scale-110"
      />
      <div class="flex justify-end items-end">
        <img
          src="https://test.create.diagnal.com/images/search.png"
          alt="search icon"
          className="w-5 h-5 transition duration-500 ease-in-out transform hover:scale-110"
          onClick={() => setSearchFieldStatus(true)}
        />
        <input
          type="text"
          name="name"
          placeholder="Search..."
          className={`w-1/3 py-2 border-b-2 border-white-600 outline-none focus:border-white-400 ${
            !showSearchField ? "hidden" : ""
          }`}
        />
      </div>
    </nav>
  );
}

export default index;
