import React, { useState } from "react";
import PropTypes from "prop-types";

/**
 * @description - Header Component.
 * @returns {Node} - Header View Component
 */
function Header() {
  const [showSearchField, setSearchFieldStatus] = useState(false);
  return (
    <nav className="flex flex-row sticky top-0 z-10 justify-center items-center bg-gradient-to-b from-black via-black w-full h-header-height">
      <div className="flex-initial flex-shrink-0">
        <img
          src="/assets/Back.png"
          alt="back icon"
          className="w-5 h-5 transition duration-500 ease-in-out transform hover:scale-110"
        />
      </div>
      <div className="flex-auto flex-shrink-0 text-left text-white whitespace-nowrap align-middle text-lg font-semibold">
        Romantic Comedy
      </div>
      <div className="flex-1 w-32">
        <img
          src="/assets/search.png"
          alt="search icon"
          className={`w-5 h-5 transition duration-500 ease-in-out transform hover:scale-110 ${
            showSearchField ? "hidden" : ""
          }`}
          onClick={() => setSearchFieldStatus(true)}
        />
        <input
          type="text"
          name="name"
          placeholder="Search..."
          className={`w-10 py-2 border-b-2 border-white-600 outline-none focus:border-white-400 bg-white ${
            !showSearchField ? "hidden" : ""
          }`}
        />
      </div>
    </nav>
  );
}

Header.propTypes = {};

export default Header;
