import React, { useState, useEffect, useRef, useCallback } from "react";
import { useScrollDirection } from "react-use-scroll-direction";
import { useDispatch, useSelector } from "react-redux";
import GridItem from "../components/GridItem";
import useFetch from "../hooks/useFetch";
import { updateHeader, updateMovieData } from "../store/actions";
import { API } from "../utils/constant";
import Loader from "../components/Loader";
import ErrorBlock from "../components/ErrorBlock";
import { filterDataByName, setKeyWithIndex } from "../utils";

function MovieList() {
  const dispatch = useDispatch();
  const [pageNumber, setPageNumber] = useState(1);
  const [list, setList] = useState([]);
  const { movieData, searchData } = useSelector((state) => state);

  const { data, hasMore, loading, error } = useFetch(API.PAGE, pageNumber);

  const { isScrolling, scrollTargetRef } = useScrollDirection();
  console.log("isScrolling ++++", isScrolling);

  useEffect(() => {
    if (data) {
      const pageItems = data?.["content-items"]?.content || [];
      dispatch(updateHeader({ title: data?.title || "" }));
      dispatch(updateMovieData(pageItems));
      const updatedList = [...list, ...pageItems];
      let filteredList = searchData?.term
        ? filterDataByName(searchData.term, updatedList)
        : updatedList;
      setList(filteredList);
    }
  }, [data]);

  const observer = useRef();
  const lastElementRef = useCallback(
    (node) => {
      if (loading) return;
      if (observer.current) observer.current.disconnect();
      observer.current = new IntersectionObserver((entries) => {
        if (entries[0].isIntersecting && hasMore) {
          setPageNumber((prevPageNumber) => prevPageNumber + 1);
        }
      });
      if (node) observer.current.observe(node);
    },
    [loading, hasMore]
  );

  useEffect(() => {
    if (searchData?.term) {
      const updatedList = filterDataByName(searchData?.term, movieData);
      setList(updatedList);
    } else {
      setList(movieData);
    }
  }, [searchData]);

  console.log("list____", list);

  return (
    <>
      <div
        className="w-full p-4 grid gap-4 grid-cols-3 sm:grid-cols-4 md:grid-cols-6 lg:grid-cols-8 xl:grid-cols-10 2xl:grid-cols-12"
        ref={scrollTarget}
      >
        {list.map((item, index) => {
          return (
            <div
              key={setKeyWithIndex(item.name, index)}
              className="min-w-full"
              {...(list.length === index + 1 && {
                ref: lastElementRef,
              })}
            >
              <GridItem item={item} />
            </div>
          );
        })}
        {loading && <Loader color="white" />}
      </div>
      {error && <ErrorBlock title="Error!" subTitle="Something went wrong." />}
    </>
  );
}

export default MovieList;
