import PropTypes from "prop-types";

const ErrorBlock = ({ title, subTitle, color }) => {
  return (
    <div class={`p-4 mb-4 text-sm text-${color} text-center`} role="alert">
      {title && <span class="font-medium">{title}</span>} {subTitle}
    </div>
  );
};

ErrorBlock.defaultProps = {
  title: "",
  subTitle: "",
  color: "white",
};

ErrorBlock.propTypes = {
  title: PropTypes.string,
  subTitle: PropTypes.string,
  color: PropTypes.string,
};

export default ErrorBlock;
