import React, { useState, useCallback, useEffect } from "react";
import PropTypes from "prop-types";
import { useSelector, useDispatch } from "react-redux";
import Icon from "../../components/Icon";
import TextField from "../../components/TextField";
import { debounce } from "../../utils";
import { updateSearch } from "../../store/actions";

const useScrollDirection = ({
  ref,
  threshold,
  debounce,
  scrollHeightThreshold,
}) => {
  threshold = threshold || 10;
  debounce = debounce || 10;
  scrollHeightThreshold = scrollHeightThreshold || 0;
  const [scrollDir, setScrollDir] = useState(null);
  const debouncedSetScrollDir = _.debounce(setScrollDir, debounce);

  useEffect(() => {
    let lastScrollY = ref?.current?.scrollTop;
    let lastScrollDir;
    let ticking = false;
    const hasScrollHeightThreshold =
      ref?.current?.scrollHeight - ref?.current?.clientHeight >
      scrollHeightThreshold;

    const updateScrollDir = () => {
      const scrollY = ref?.current?.scrollTop;
      if (
        Math.abs(scrollY - lastScrollY) < threshold ||
        !hasScrollHeightThreshold
      ) {
        ticking = false;
        return;
      }
      const newScroll = scrollY > lastScrollY ? "down" : "up";
      if (newScroll !== lastScrollDir) {
        debouncedSetScrollDir(newScroll);
      }
      lastScrollY = scrollY > 0 ? scrollY : 0;
      lastScrollDir = newScroll;
      ticking = false;
    };

    const onScroll = () => {
      if (!ticking) {
        window.requestAnimationFrame(updateScrollDir);
        ticking = true;
      }
    };

    ref?.current?.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  return scrollDir;
};

/**
 * @description - Header Component.
 * @returns {Node} - Header View Component
 */
function Header() {
  const dispatch = useDispatch();
  const [showSearchField, setSearchFieldStatus] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const headerData = useSelector((state) => state.headerData);

  const ref = useRef();
  const scrollDirection = useScrollDirection({
    ref,
  });

  const setData = (value) => {
    dispatch(updateSearch(value));
  };

  const debouncedSave = useCallback(
    debounce((nextValue) => setData(nextValue), 300),
    []
  );

  const handleInputChange = (e) => {
    setSearchTerm(e.target.value);
    debouncedSave(e.target.value);
  };

  useEffect(() => {
    console.log("isScrolling++++", verticalScrollDirection);
  }, [verticalScrollDirection]);

  return (
    <div
      className="flex flex-row sticky top-0 z-10 justify-center items-center space-x-4 p-4 bg-gradient-to-b from-black via-black w-full h-header-height"
      ref={ref}
    >
      <div className="flex-initial flex-shrink-0">
        <Icon src="Back.png" alt="back icon" />
      </div>
      <div className="flex-auto flex-shrink-0 text-left text-white whitespace-nowrap align-middle text-lg font-semibold">
        {headerData?.title}
      </div>
      <div className="flex flex-row flex-initial justify-end">
        <div
          className={`${
            showSearchField ? "hidden" : ""
          } flex flex-row flex-initial justify-end items-end space-x-2`}
          onClick={() => setSearchFieldStatus(!showSearchField)}
        >
          <Icon src="search.png" alt="search icon" />
        </div>
        <div
          className={`${
            !showSearchField ? "hidden" : ""
          } flex flex-initial justify-end items-end`}
        >
          <TextField
            handleChange={handleInputChange}
            val={searchTerm}
            className="w-11/12 rounded-md p-1 px-1.5 transition duration-1000 ease-in transform scale-100 bg-black border-b rounded-none text-white hover:outline-0"
            placeholder="Search"
          />
        </div>
      </div>
    </div>
  );
}

Header.propTypes = {};

export default Header;
