import React, { useState, useEffect, useRef, useCallback } from "react";
import GridItem from "../components/GridItem";
import useFetch from "../hooks/useFetch";
import { updateMovieList } from "../store/actions";
import { useDispatch, useSelector } from "react-redux";
import { API } from "../utils/constant";
import Loader from "../components/Loader";
function MovieList(props) {
  const dispatch = useDispatch();
  const [pageNumber, setPageNumber] = useState(1);
  const [movieData, setMovieData] = useState([]);

  const { data, hasMore, loading, error } = useFetch(API.PAGE, pageNumber);

  useEffect(() => {
    if (data) {
      dispatch(updateMovieList(data));
      const pageItems = data?.["content-items"]?.content || [];
      setMovieData([...movieData, ...pageItems]);
    }
  }, [data]);

  const observer = useRef();
  const lastBookElementRef = useCallback(
    (node) => {
      if (loading) return;
      if (observer.current) observer.current.disconnect();
      observer.current = new IntersectionObserver((entries) => {
        if (entries[0].isIntersecting && hasMore) {
          console.log("visible+++");
          setPageNumber((prevPageNumber) => prevPageNumber + 1);
        }
      });
      if (node) observer.current.observe(node);
    },
    [loading, hasMore]
  );

  console.log("response++++", data);

  return (
    <>
      <div className="w-full p-4 grid gap-4 grid-cols-3 sm:grid-cols-4 md:grid-cols-6 lg:grid-cols-8 xl:grid-cols-10 2xl:grid-cols-12">
        {/* {movieData.map((item, index) => {
        const key = `${item.name.replaceAll(" ", "-").toLowerCase()}-${index}`;
        return (
          <div
            key={key}
            {...(movieData.length === index + 1 && { ref: lastBookElementRef })}
          >
            <GridItem item={item} />
          </div>
        );
      })} */}
      </div>
      {loading && <Loader color="white" />}
      <div
        class="p-4 mb-4 text-sm text-gray-700 bg-gray-100 rounded-lg dark:bg-gray-700 dark:text-gray-300"
        role="alert"
      >
        <span class="font-medium">Dark alert!</span> Change a few things up and
        try submitting again.
      </div>
      <div>{error && "Error"}</div>
    </>
  );
}

export default MovieList;
