export const debounce = (fn, d) => {
  let timer;
  return function () {
    let context = this,
      args = arguments;
    clearTimeout(timer);
    timer = setTimeout(() => {
      fn.apply(context, args);
    }, d);
  };
};

export const filterDataByName = (term, array) =>
  array.filter(({ name }) => name.toLowerCase().includes(term.toLowerCase()));
